//to get a value of a PIE
let PIE = Math.PI;
console.log(PIE);

let actualValue = 1245.615;

//ceiling Value
let ceilValue= Math.ceil(actualValue);
console.log(ceilValue);

//Floor
let floorValue= Math.floor(actualValue);
console.log(floorValue);

//To get round of value
let roundValue=Math.round(actualValue);
console.log(roundValue);

//To get truncated value
let truncatedValue = Math.trunc(actualValue);
console.log(truncatedValue);

//To find m raised to n (2 raised to 3)
let powerValue = Math.pow(2,3);
console.log(powerValue);

//To fins square root of a value. Example to find square root of a 4
let squareRootValue=Math.sqrt(4);
console.log(squareRootValue);

//To determine that the number is positive or negative or a zero
let signValue = Math.sign(-45);
console.log(signValue);

signValue = Math.sign(45);
console.log(signValue);

signValue = Math.sign(0);
console.log(signValue);

let answer = 18%12;
console.log(answer);

answer = 18/12;
console.log(answer);

answer = Math.floor(18/12);
console.log(answer);

answer = Math.ceil(18/12);
console.log(answer);

